mapper.update:
UPDATE t_board
SET title = #{title}, content = #{content}, view_cnt = #{viewCnt}, game_id = #{gameId,jdbcType=INTEGER}
WHERE no = #{no}

mapper.select:
SELECT *
FROM (
    SELECT a.*, ROW_NUMBER() OVER (ORDER BY reg_date DESC) AS rnum
    FROM t_board a
)
WHERE rnum BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE t_board
SET view_cnt = view_cnt + 1
WHERE no = #{no}
